        #region Private Methods

        private char EncryptChar(char charToEncrypt, char charPassword)
        {
            int posicionChar = CharToIntConvert(charToEncrypt);
            char[] fila = _vigenereTable[charPassword];
            return fila[posicionChar];
        }

        private char DescryptChar(char charToDecrypt, char charPassword)
        {
            char[] fila = _vigenereTable[charPassword];
            int pos = 0;
            for (; pos <= fila.Count() - 1; pos++)
            {
                if (fila[pos] == charToDecrypt)
                    break;
            }

            char decrypt = IntToCharConvert(pos);
            return decrypt;
        }

        private int CharToIntConvert(char unChar)
        {
            char[] reference = _vigenereTable.Where(a => a.Value[0] == 'a').FirstOrDefault().Value;
            int pos = 0;
            for (; pos <= reference.Count() - 1; pos++)
            {
                if (reference[pos] == unChar)
                    break;
            }

            return pos;
        }

        private char IntToCharConvert(int pos)
        {
            char[] reference = _vigenereTable.Where(a => a.Value[0] == 'a').FirstOrDefault().Value;
            return reference[pos];
        }

        #endregion

        #region Public Methods IEncrypterDecrypter

        public char[] GetValidCharacters()
        {
            return _vigenereTable['a'];
        }

        public string EncryptarCadena(string cadena, string password)
        {
            if (string.IsNullOrEmpty(cadena)) return string.Empty;

            int posPass = -1;
            char[] encyptada = new char[cadena.Length];

            for (int posCad = 0; posCad <= cadena.Length - 1; posCad++)
            {
                if (posPass < password.Length - 1)
                    posPass++;
                else
                    posPass = 0;

                char charToEnctrypt = cadena[posCad];
                char charPass = password[posPass];
                char charEncryptado = EncryptChar(charToEnctrypt, charPass);
                encyptada[posCad] = charEncryptado;
            }

            return new string(encyptada);
        }

        public string DecryptarCadena(string cadena, string password)
        {
            if (string.IsNullOrEmpty(cadena)) return string.Empty;

            int posPass = -1;
            char[] decryptada = new char[cadena.Length];

            for (int posCad = 0; posCad <= cadena.Length - 1; posCad++)
            {
                if (posPass < password.Length - 1)
                    posPass++;
                else
                    posPass = 0;

                char charToDecrypt = cadena[posCad];
                char charPass = password[posPass];
                char charDecryptado = DescryptChar(charToDecrypt, charPass);
                decryptada[posCad] = charDecryptado;
            }

            return new string(decryptada);
        }

